import React, { useEffect, useState } from "react";
import { Button, IconButton } from "@mui/material";
import { useNavigate, useParams } from "react-router";
import { Note, deleteNote, getNoteById, updateNote } from "../api";
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

const NotePage: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();
    const [note, setNote] = useState<Note | null>(null);
    const [isEditing, setIsEditing] = useState(false);
    const [editedNote, setEditedNote] = useState({ title: '', body: '' });

    useEffect(() => {
        const fetchNoteDetails = async () => {
            try {
                if (id !== undefined) {
                    const data: Note = await getNoteById(parseInt(id));
                    setNote(data);
                    setEditedNote({ title: data.title, body: data.body });
                }
            } catch (error) {
                console.error('Error fetching note details:', error);
            }
        };

        fetchNoteDetails();
    }, [id]);

    const handleDeleteNote = async () => {
        try {
            if (id !== undefined) {
                await deleteNote(parseInt(id));
                navigate('/');
            }
        } catch (error) {
            console.error('Error deleting note:', error);
        }
    };

    const handleEditNote = async () => {
        try {
            if (id !== undefined) {
                console.log(editedNote.body.length)
                const updatedNote: Note = await updateNote(parseInt(id), {
                    title: editedNote.title,
                    body: editedNote.body,
                });
                setNote(updatedNote);
                setIsEditing(false);
            }
        } catch (error) {
            console.error('Error updating note:', error);
        }
    };

    if (!note) {
        return <div>Loading...</div>;
    }

    return (
        <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
                <IconButton color="primary" onClick={() => navigate('/')}>
                    <ArrowBackIcon />
                </IconButton>
                <h1 style={{ color: '#3f51b5', textAlign: 'center', margin: '0 20px', fontSize: '24px' }}>Note Details</h1>
            </div>
            <div style={{ backgroundColor: '#f5f5f5', padding: '20px', borderRadius: '5px', marginBottom: '20px' }}>
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                    {isEditing ? (
                        <input
                            type="text"
                            value={editedNote.title}
                            onChange={(e) => setEditedNote({ ...editedNote, title: e.target.value })}
                        />
                    ) : (
                        <h2 style={{ fontSize: '24px', marginBottom: '10px' }}>{note.title}</h2>
                    )}
                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        {isEditing ? (
                            <Button variant="contained" color="primary" onClick={handleEditNote}>
                                Save
                            </Button>
                        ) : (
                            <IconButton color="primary" onClick={() => setIsEditing(true)}>
                                <EditIcon />
                            </IconButton>
                        )}
                        <IconButton color="secondary" onClick={handleDeleteNote}>
                            <DeleteIcon />
                        </IconButton>
                    </div>
                </div>
                {isEditing ? (
                    <div>
                        <textarea
                            value={editedNote.body}
                            onChange={(e) => {
                                const inputbody = e.target.value;
                                // Limit body to 1000 characters
                                if (inputbody.length <= 1000) {
                                    setEditedNote({ ...editedNote, body: inputDescription });
                                }
                            }}
                            rows={4}
                            style={{ width: '100%', marginBottom: '10px' }}
                            maxLength={1000}
                        />
                        <p style={{ fontSize: '14px', color: editedNote.description.length <= 1000 ? '#555' : 'red' }}>
                            {1000 - editedNote.description.length} characters remaining
                        </p>
                    </div>
                ) : (
                    <p style={{ fontSize: '16px', color: '#555' }}>{note.description}</p>
                )}

            </div>
        </div>
    );
};

export default NotePage;
