import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  IconButton,
  OutlinedInput,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import { Note, createNote, deleteNote, getNotes } from "../api";
import colors from "../colors";
import DeleteConfirmationDialogBox from "../components/DeleteConfirmDialogBox";

const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [dialogOpen, setDialogOpen] = useState(false);
  coconst [noteToDelete, setNoteToDelete] = useState({ id: -1, title: "" });nst [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);

  const [newNote, setNewNote] = useState({ title: "", body: "" });
  const navigate = useNavigate();
  const [isSaveButtonDisabled, setIsSaveButtonDisabled] = useState(true);
  const MAX_body_LENGTH = 1000;

  // Use useEffect to fetch updated notes whenever newNote changes
  useEffect(() => {
    const fetchData = async () => {
      try {
        const updatedNotes = await getNotes();
        setNotes(updatedNotes); // Update the state with the new notes data
      } catch (error) {
        // Handle error fetching notes
        console.error("Error fetching notes:", error);
      }
    };

    // Fetch updated notes whenever newNote changes
    fetchData();
  }, [newNote]); // Dependency array with newNote ensures the effect runs whenever newNote changes

  const handleDialogOpen = () => {
    setDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
  };

  const handleDeleteConfirmationOpen = (id: number, title: string) => {
    setNoteToDelete({ id, title });
    setDeleteConfirmationOpen(true);
  };

  const handleDeleteConfirmationClose = () => {
    setNoteToDelete({ id: -1, title: "" });
    setDeleteConfirmationOpen(false);
  };

  const handleDeleteNote = async () => {
    try {
      // Delete note using the API function
      await deleteNote(noteToDelete.id);
      const updatedNotes = notes.filter((note) => note.id !== noteToDelete.id);
      setNotes(updatedNotes);
      handleDeleteConfirmationClose();
    } catch (error) {
      // Handle error deleting note
      console.error("Error deleting note:", error);
    }
  };

  const handleSaveNote = async () => {
    try {
      // Truncate body if it exceeds 1000 characters
      const truncatedBody = newNote.body.slice(0, MAX_body_LENGTH);

      const savedNote = await createNote({
        title: newNote.title,
        body: truncatedBody,
      });

      setDialogOpen(false);
      setNewNote({ title: newNote.title, body: truncatedBody });
    } catch (error) {
      // Handle error creating note
      console.error("Error creating note:", error);
    }
  };

  return (
    <div
      style={{
        padding: "20px",
        maxWidth: "600px",
        margin: "0 auto",
        backgroundColor: colors.lightBackground,
      }}
    >
      <h1
        style={{
          color: colors.primary,
          textAlign: "center",
          marginBottom: "20px",
        }}
      >
        Notes
      </h1>
      {notes.map((note) => (
        <div key={note.id}>
          <div
            style={{
              backgroundColor: colors.secondary, // Set the background color to secondary color from the palette
              padding: "10px",
              borderRadius: "5px",
              marginBottom: "10px",
              position: "relative",
            }}
            onClick={() => navigate(`/note/${note.id}`)}
          >
            <div
              style={{
                cursor: "pointer",
                color: colors.primary, // Set the text color to primary color from the palette
                flexGrow: 1,
                marginRight: "10px",
              }}
            >
              <h2 style={{ margin: "0", fontSize: "18px" }}>{note.title}</h2>
            </div>
            <IconButton
              color="secondary"
              onClick={(e) => {
                e.stopPropagation(); // Prevent click propagation to the parent div
                handleDeleteConfirmationOpen(note.id, note.title);
              }}
              style={{
                position: "absolute",
                top: "50%",
                right: "10px",
                transform: "translateY(-50%)",
                color: colors.accent, // Set the icon color to accent color from the palette
              }}
            >
              <DeleteIcon />
            </IconButton>
          </div>
        </div>
      ))}

      <Fab
        color="primary"
        aria-label="add"
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: colors.accent,
        }}
        onClick={handleDialogOpen}
      >
        <AddIcon style={{ color: colors.secondary }} />
      </Fab>

      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle
          style={{
            backgroundColor: colors.lightBackground,
            color: colors.primary,
          }}
        >
          Add New Note
        </DialogTitle>
        <DialogContent
          style={{
            backgroundColor: colors.lightBackground,
            color: colors.primary,
          }}
        >
          <TextField
            fullWidth
            margin="normal"
            variant="outlined"
            value={newNote.title}
            sx={{ backgroundColor: colors.secondary }}
            InputProps={{
              style: {
                color: colors.primary, // Set the text color to colors.primary
                border: `1px solid ${colors.lightBackground}`, // Set the border color to colors.lightBackground
              },
            }}
            placeholder="Title"
            onChange={(e) => {
              setNewNote({ ...newNote, title: e.target.value });
              setIsSaveButtonDisabled(
                e.target.value.trim() === "" || newNote.body.trim() === ""
              );
            }}
          />

          <TextField
            fullWidth
            multiline
            rows={4}
            margin="normal"
            variant="outlined"
            placeholder="Body"
            value={newNote.body}
            sx={{ backgroundColor: colors.secondary }}
            InputProps={{
              style: {
                color: colors.primary, // Set the text color to colors.primary
                border: `1px solid ${colors.lightBackground}`, // Set the border color to colors.lightBackground
              },
            }}
            onChange={(e) => {
              setNewNote({ ...newNote, body: e.target.value });
              setIsSaveButtonDisabled(
                newNote.title.trim() === "" || e.target.value.trim() === ""
              );
            }}
          />
        </DialogContent>
        <DialogActions style={{ backgroundColor: colors.secondary }}>
          <Button onClick={handleDialogClose} sx={{ color: colors.accent }}>
            Cancel
          </Button>
          <Button
            onClick={handleSaveNote}
            sx={{ color: colors.accent }}
            disabled={isSaveButtonDisabled}
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>

      <DeleteConfirmationDialogBox
        onOpenDialogBox={deleteConfirmationOpen}
        onClickDeleteButton={handleDeleteNote}
        onCloseDialogBox={handleDeleteConfirmationClose}
        noteToDelete={{ id: noteToDelete.id, title: noteToDelete.title }}
      />
    </div>
  );
};

export default HomePage;
