import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  IconButton,
  OutlinedInput,
  Stack,
  Box,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import { Note, createNote, deleteNote, getNotes } from "../api";
import colors from "../colors";
import DeleteConfirmationDialogBox from "../components/DeleteConfirmDialogBox";
import NoteDialogBox from "../components/NoteDialogBox";

const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
  const [noteToDelete, setNoteToDelete] = useState({ id: -1, title: "" });
  const [newNote, setNewNote] = useState({ title: "", body: "" });
  const navigate = useNavigate();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editedNote, setEditedNote] = useState({ title: "", body: "" });
  const [page, setPage] = useState(2); // Current page number
  const [pageSize, setPageSize] = useState(10); // Number of notes per page
  const [loading, setLoading] = useState(false); // State to track loading status

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditedNote({ title: "", body: "" });
  };
  const MAX_body_LENGTH = 1000;

  const fetchData = async () => {
    console.log("Fetching data for page:", page, "with pageSize:", pageSize);
    if (loading) {
      console.log("Already loading data, skipping duplicate request.");
      return; // If already loading, prevent duplicate requests
    }
    setLoading(true); // Set loading state to true while fetching data
    try {
      const updatedNotes = await getNotes(page, pageSize); // Fetch notes based on pagination parameters
      setNotes((prevNotes) => [...prevNotes, ...updatedNotes]); // Append new notes to the existing notes data
      console.log("API response:", updatedNotes);
      setPage(page + 1); // Increment the page number for the next page
    } catch (error) {
      // Handle error fetching notes
      console.error("Error fetching notes:", error);
    } finally {
      setLoading(false); // Set loading state back to false after fetching data
    }
  };

  useEffect(() => {
    fetchData();
  }, []);


  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop !==
        document.documentElement.offsetHeight ||
      loading
    ) {
      return;
    }
    fetchData();
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [loading]);

  const handleDeleteConfirmationOpen = (id: number, title: string) => {
    setNoteToDelete({ id, title });
    setDeleteConfirmationOpen(true);
  };

  const handleDeleteConfirmationClose = () => {
    setNoteToDelete({ id: -1, title: "" });
    setDeleteConfirmationOpen(false);
  };

  const handleDeleteNote = async () => {
    try {
      // Delete note using the API function
      await deleteNote(noteToDelete.id);
      const updatedNotes = notes.filter((note) => note.id !== noteToDelete.id);
      setNotes(updatedNotes);
      handleDeleteConfirmationClose();
    } catch (error) {
      // Handle error deleting note
      console.error("Error deleting note:", error);
    }
  };

  const handleSaveNote = async (noteData: { title: string; body: string }) => {
    try {
      // Truncate body if it exceeds 1000 characters
      const truncatedBody = noteData.body.slice(0, MAX_body_LENGTH);

      const savedNote = await createNote({
        title: noteData.title,
        body: truncatedBody,
      });

      setIsDialogOpen(false);
      setNewNote({ title: noteData.title, body: truncatedBody });
      setEditedNote({ title: "", body: "" });
    } catch (error) {
      // Handle error creating note
      console.error("Error creating note:", error);
    }
  };

  return (
    <Box
      sx={{
        backgroundColor: colors.lightBackground,
        width: "100%",
        height: "100vh",
        position: "fixed",
        top: 0,
      }}
    >
      <Box
        sx={{
          padding: "10px",
          margin: "0 auto",
          width: "65%",
          height: "90%",
          paddingBottom: "20px",
        }}
      >
        <h1
          style={{
            color: colors.primary,
            textAlign: "center",
            marginBottom: "20px",
          }}
        >
          Notes
        </h1>
        <Box
          sx={{
            flex: 1,
            overflow: "auto",
            height: "80%",
            scrollbarWidth: "thin", // Set the width of the scrollbar
            WebkitOverflowScrolling: "touch", // Enable smooth scrolling on iOS devices
            "&::-webkit-scrollbar": {
              width: "3px", // Width of the scrollbar track
            },
            "&::-webkit-scrollbar-thumb": {
              backgroundColor: colors.primary, // Color of the scrollbar thumb
            },
          }}
        >
          {notes.map((note) => (
            <div
              key={note.id}
              style={{
                backgroundColor: colors.secondary,
                padding: "10px",
                borderRadius: "5px",
                marginBottom: "10px",
                position: "relative",
                display: "flex",
                alignItems: "center", // Align items vertically within each list item
                marginRight: "5px",
              }}
              onClick={() => navigate(`/note/${note.id}`)}
            >
              <div
                style={{
                  cursor: "pointer",
                  color: colors.primary, // Set the text color to primary color from the palette
                  flexGrow: 1,
                  marginRight: "10px",
                }}
              >
                <h2 style={{ margin: "0", fontSize: "18px" }}>{note.title}</h2>
              </div>
              <IconButton
                color="secondary"
                onClick={(e) => {
                  e.stopPropagation(); // Prevent click propagation to the parent div
                  handleDeleteConfirmationOpen(note.id, note.title);
                }}
                style={{
                  position: "absolute",
                  top: "50%",
                  right: "10px",
                  transform: "translateY(-50%)",
                  color: colors.accent, // Set the icon color to accent color from the palette
                }}
              >
                <DeleteIcon />
              </IconButton>
            </div>
          ))}
          {loading && <p>Loading...</p>} {/* Loading indicator */}
        </Box>
        <Fab
          color="primary"
          aria-label="add"
          style={{
            position: "fixed",
            bottom: "20px",
            right: "20px",
            backgroundColor: colors.accent,
          }}
          onClick={handleOpenDialog}
        >
          <AddIcon style={{ color: colors.secondary }} />
        </Fab>
        <NoteDialogBox
          open={isDialogOpen}
          onClose={handleCloseDialog}
          onSave={handleSaveNote}
          title={editedNote.title}
          body={editedNote.body}
        />

        <DeleteConfirmationDialogBox
          onOpenDialogBox={deleteConfirmationOpen}
          onClickDeleteButton={handleDeleteNote}
          onCloseDialogBox={handleDeleteConfirmationClose}
          noteToDelete={{ id: noteToDelete.id, title: noteToDelete.title }}
        />
      </Box>
    </Box>
  );
};

export default HomePage;
