import React from "react";
import { render, fireEvent, waitFor } from "@testing-library/react";
import "@testing-library/jest-dom/extend-expect";

import NoteDialogBox from "./NoteDialogBox"; // Adjust the import path based on your project structure

describe("NoteDialogBox Component", () => {
  test("renders without crashing", () => {
    const { getByText } = render(
      <NoteDialogBox open={true} onClose={() => {}} onSave={() => {}} title="" body="" />
    );
    const addNoteText = getByText("Add New Note");
    expect(addNoteText).toBeInTheDocument();
  });

  test("enforces character limitations for title and body", async () => {
    const onSaveMock = jest.fn();
    const onCloseMock = jest.fn();

    const { getByPlaceholderText, getByText } = render(
      <NoteDialogBox open={true} onClose={onCloseMock} onSave={onSaveMock} title="" body="" />
    );

    const titleInput = getByPlaceholderText("Title");
    const bodyInput = getByPlaceholderText("Body");
    const saveButton = getByText("Save");

    // Enter more than MAX_TITLE_CHARACTERS characters in the title input
    fireEvent.change(titleInput, {
      target: { value: "a".repeat(200) },
    });

    // Enter more than MAX_CHARACTERS characters in the body input
    fireEvent.change(bodyInput, {
      target: { value: "a".repeat(1100) },
    });

    // Wait for the component to re-render after state updates
    await waitFor(() => {
      // Save button should have 'disabled' attribute set to true
      expect(saveButton).toHaveAttribute("disabled");
    });

    // Enter valid title and body within character limitations
    fireEvent.change(titleInput, {
      target: { value: "Valid Title" },
    });

    fireEvent.change(bodyInput, {
      target: { value: "Valid Body" },
    });

    // Wait for the component to re-render after state updates
    await waitFor(() => {
      // Save button should not have 'disabled' attribute
      expect(saveButton).not.toHaveAttribute("disabled");
    });

    // Verify onSave function is called when clicking the save button
    fireEvent.click(saveButton);
    expect(onSaveMock).toHaveBeenCalledWith({
      title: "Valid Title",
      body: "Valid Body",
    });

    // Verify onClose function is called when clicking the cancel button
    const cancelButton = getByText("Cancel");
    fireEvent.click(cancelButton);
    expect(onCloseMock).toHaveBeenCalled();
  });
});
