import React, { useState } from "react";
import {
  Fab,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Divider,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import MenuIcon from "@mui/icons-material/Menu";
import { Note, createNote, deleteNote, getNotes } from "../api";
import NoteList from "./NoteList";

const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);
  const [newNote, setNewNote] = useState({ title: "", body: "" });

  const handleDrawerToggle = () => {
    setDrawerOpen(!drawerOpen);
  };

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setDrawerOpen(false);
  };

  // Rest of your code...

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <IconButton
        edge="start"
        color="inherit"
        aria-label="menu"
        onClick={handleDrawerToggle}
        style={{ marginBottom: "10px" }}
      >
        <MenuIcon />
      </IconButton>
      <Drawer anchor="left" open={drawerOpen} onClose={handleDrawerToggle}>
        <List>
          {notes.map((note) => (
            <ListItem button key={note.id} onClick={() => handleNoteClick(note)}>
              <ListItemText primary={note.title} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      {/* Main content */}
      <div style={{ marginLeft: drawerOpen ? 240 : 0 }}>
        {selectedNote ? (
          <div>
            <h1>{selectedNote.title}</h1>
            <p>{selectedNote.body}</p>
          </div>
        ) : (
          <NoteList notes={notes} onDeleteNote={handleDeleteNote} onNoteClick={handleNoteClick} />
        )}
      </div>

      {/* Add new note button */}
      <Fab
        color="primary"
        aria-label="add"
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: "#f50057",
        }}
        onClick={handleDialogOpen}
      >
        <AddIcon style={{ color: "#fff" }} />
      </Fab>

      {/* Rest of your code... */}
    </div>
  );
};

export default HomePage;
