import React, { useEffect, useState } from "react";
import {
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from "@mui/material";
import { useNavigate, useParams } from "react-router";
import { Note, deleteNote, getNoteById, updateNote } from "../api";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import colors from "../colors";
import DeleteConfirmationDialogBox from "../components/DeleteConfirmDialogBox";

const NotePage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [note, setNote] = useState<Note | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedNote, setEditedNote] = useState({ title: "", body: "" });
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
  const [noteToDelete, setNoteToDelete] = useState({ id: -1, title: "" });

  useEffect(() => {
    const fetchNoteDetails = async () => {
      try {
        if (id !== undefined) {
          const data: Note = await getNoteById(parseInt(id));
          setNote(data);
          setEditedNote({ title: data.title, body: data.body });
        }
      } catch (error) {
        console.error("Error fetching note details:", error);
      }
    };

    fetchNoteDetails();
  }, [id]);

  const handleDeleteConfirmationOpen = (id: number, title: string) => {
    setNoteToDelete({ id, title });
    setDeleteConfirmationOpen(true);
  };

  const handleDeleteConfirmationClose = () => {
    setNoteToDelete({ id: -1, title: "" });
    setDeleteConfirmationOpen(false);
    navigate("/note");
  };

  const handleDeleteNote = async () => {
    try {
      // Delete note using the API function
      await deleteNote(noteToDelete.id);
      handleDeleteConfirmationClose();
    } catch (error) {
      // Handle error deleting note
      console.error("Error deleting note:", error);
    }
  };

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
  };

  const handleSaveNote = async () => {
    try {
      if (id !== undefined) {
        const updatedNote: Note = await updateNote(parseInt(id), {
          title: editedNote.title,
          body: editedNote.body,
        });
        setNote(updatedNote);
        setIsDialogOpen(false);
      }
    } catch (error) {
      console.error("Error updating note:", error);
    }
  };

  const handleEditNote = () => {
    handleOpenDialog();
  };

  if (!note) {
    return <div>Loading...</div>;
  }

  return (
    <div
      style={{
        padding: "20px",
        maxWidth: "600px",
        margin: "0 auto",
        backgroundColor: colors.lightBackground,
      }}
    >
      <div
        style={{ display: "flex", alignItems: "center", marginBottom: "20px" }}
      >
        <IconButton
          sx={{ color: colors.primary }}
          onClick={() => navigate("/")}
        >
          <ArrowBackIcon />
        </IconButton>
        <h1
          style={{
            color: colors.primary,
            textAlign: "center",
            margin: "0 20px",
            fontSize: "24px",
          }}
        >
          Note Details
        </h1>
      </div>
      <div
        style={{
          backgroundColor: colors.secondary,
          padding: "20px",
          borderRadius: "5px",
          marginBottom: "20px",
        }}
      >
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <h2
            style={{
              fontSize: "24px",
              marginBottom: "10px",
              color: colors.primary,
            }}
          >
            {note.title}
          </h2>
          <div style={{ display: "flex", alignItems: "center" }}>
            <IconButton sx={{ color: colors.accent }} onClick={handleEditNote}>
              <EditIcon />
            </IconButton>
            <IconButton
              sx={{ color: colors.accent }}
              onClick={(e) => {
                e.stopPropagation(); // Prevent click propagation to the parent div
                handleDeleteConfirmationOpen(note.id, note.title);
              }}
            >
              <DeleteIcon />
            </IconButton>
          </div>
        </div>
        <p style={{ fontSize: "16px", color: colors.primary }}>{note.body}</p>
      </div>

      <DeleteConfirmationDialogBox
        onOpenDialogBox={deleteConfirmationOpen}
        onClickDeleteButton={handleDeleteNote}
        onCloseDialogBox={handleDeleteConfirmationClose}
        noteToDelete={{ id: noteToDelete.id, title: noteToDelete.title }}
      />

      <Dialog open={isDialogOpen} onClose={handleCloseDialog}>
        <DialogTitle
          style={{
            backgroundColor: colors.lightBackground,
            color: colors.primary,
          }}
        >
          Edit Note
        </DialogTitle>
        <DialogContent
          style={{
            backgroundColor: colors.lightBackground,
            color: colors.primary,
          }}
        >
          <TextField
            fullWidth
            variant="outlined"
            margin="dense"
            value={editedNote.title}
            sx={{ backgroundColor: colors.secondary }}
            InputProps={{
              style: {
                color: colors.primary, // Set the text color to colors.primary
                border: `1px solid ${colors.lightBackground}`, // Set the border color to colors.lightBackground
              },
            }}
            placeholder="Title"
            onChange={(e) =>
              setEditedNote({ ...editedNote, title: e.target.value })
            }
          />
          <TextField
            fullWidth
            multiline
            rows={4}
            variant="outlined"
            margin="dense"
            value={editedNote.body}
            sx={{ backgroundColor: colors.secondary }}
            InputProps={{
              style: {
                color: colors.primary, // Set the text color to colors.primary
                border: `1px solid ${colors.lightBackground}`, // Set the border color to colors.lightBackground
              },
            }}
            onChange={(e) =>
              setEditedNote({ ...editedNote, body: e.target.value })
            }
          />
        </DialogContent>
        <DialogActions style={{ backgroundColor: colors.secondary }}>
          <Button onClick={handleCloseDialog} sx={{ color: colors.accent }}>
            Cancel
          </Button>
          <Button onClick={handleSaveNote} sx={{ color: colors.accent }}>
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default NotePage;
