import axios, { AxiosResponse } from 'axios';

const API_BASE_URL = 'http://localhost:3001';

export interface Note {
    id: number;
    title: string;
    body: string;
    createdDate: Date;
}

export const getNoteById = async (id: number): Promise<Note> => {
    try {
        const response: AxiosResponse<Note> = await axios.get(`${API_BASE_URL}/notes/${id}`);
        return response.data;
    } catch (error) {
        console.error(`Error fetching note with ID ${id}:`, error);
        throw error;
    }
};

export const getNotes = async (): Promise<Note[]> => {
    try {
        const response: AxiosResponse<Note[]> = await axios.get(`${API_BASE_URL}/notes`);
        return response.data;
    } catch (error) {
        console.error('Error fetching notes:', error);
        throw error;
    }
};

export const createNote = async (note: { title: string; body: string }): Promise<Note> => {
    try {
        const response: AxiosResponse<Note> = await axios.post(`${API_BASE_URL}/notes`, note);
        return response.data;
    } catch (error) {
        console.error('Error creating note:', error);
        throw error;
    }
};

export const updateNote = async (id: number, note: { title: string; body: string }): Promise<Note> => {
    try {
        const response: AxiosResponse<Note> = await axios.patch(`${API_BASE_URL}/notes/${id}`, note);
        return response.data;
    } catch (error) {
        console.error('Error updating note:', error);
        throw error;
    }
};

export const deleteNote = async (id: number): Promise<void> => {
    try {
        await axios.delete(`${API_BASE_URL}/notes/${id}`);
    } catch (error) {
        console.error('Error deleting note:', error);
        throw error;
    }
};
