import React, { useEffect, useRef, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  IconButton,
  OutlinedInput,
  Stack,
  Box,
  Typography,
  Pagination,
  useTheme,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import { Note, createNote, deleteNote, getNotes } from "../api";
import colors from "../colors";
import DeleteConfirmationDialogBox from "../components/DeleteConfirmDialogBox";
import NoteDialogBox from "../components/NoteDialogBox";

const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
  const [noteToDelete, setNoteToDelete] = useState({ id: -1, title: "" });
  const [newNote, setNewNote] = useState({ title: "", body: "" });
  const navigate = useNavigate();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editedNote, setEditedNote] = useState({ title: "", body: "" });
  const [totalCount, setTotalCount] = useState(0);
  const theme = useTheme();

  //Pagination
  const itemsPerPage = 10;
  const homePagePageListRef = useRef<HTMLDivElement>(null);
  const [homePageFetchNextPage, setHomePageFetchNextPage] = useState({
    page: 1,
    limit: itemsPerPage,
  });

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditedNote({ title: "", body: "" });
  };
  const MAX_body_LENGTH = 1000;

  const fetchData = async () => {
    try {
      const updatedNotes = await getNotes(
        homePageFetchNextPage.page,
        homePageFetchNextPage.limit
      );
      setTotalCount(updatedNotes.totalCount);
      setNotes(updatedNotes.notes); // Update the state with the new notes data
    } catch (error) {
      // Handle error fetching notes
      console.error("Error fetching notes:", error);
    }
  };

  // Use useEffect to fetch updated notes whenever newNote changes
  useEffect(() => {
    // Fetch updated notes whenever newNote changes
    fetchData();
  }, [newNote]); // Dependency array with newNote ensures the effect runs whenever newNote changes

  useEffect(() => {
    fetchData();
  }, [homePageFetchNextPage.page, homePageFetchNextPage.limit, fetchData]);

  const handleDeleteConfirmationOpen = (id: number, title: string) => {
    setNoteToDelete({ id, title });
    setDeleteConfirmationOpen(true);
  };

  const handleDeleteConfirmationClose = () => {
    setNoteToDelete({ id: -1, title: "" });
    setDeleteConfirmationOpen(false);
  };

  const handleDeleteNote = async () => {
    try {
      // Delete note using the API function
      await deleteNote(noteToDelete.id);
      const updatedNotes = notes.filter((note) => note.id !== noteToDelete.id);
      setNotes(updatedNotes);
      handleDeleteConfirmationClose();
    } catch (error) {
      // Handle error deleting note
      console.error("Error deleting note:", error);
    }
  };

  const handleSaveNote = async (noteData: { title: string; body: string }) => {
    try {
      // Truncate body if it exceeds 1000 characters
      const truncatedBody = noteData.body.slice(0, MAX_body_LENGTH);

      const savedNote = await createNote({
        title: noteData.title,
        body: truncatedBody,
      });

      setIsDialogOpen(false);
      setNewNote({ title: noteData.title, body: truncatedBody });
      setEditedNote({ title: "", body: "" });
    } catch (error) {
      // Handle error creating note
      console.error("Error creating note:", error);
    }
  };

  return (
    <Box
      sx={{
        display: "flex",
        flex: 1,
      }}
    >
      <Stack
        sx={{
          position: "absolute",
          top: "0px",
          right: "0",
          left: "0",
          bottom: "0px",
          padding: "30px",
          backgroundColor: colors.lightBackground,
        }}
      >
        <Typography
          component={"h1"}
          sx={{
            color: colors.primary,
            textAlign: "center",
            marginBottom: "20px",
            lineHeight: "56px",
            fontSize: "45px",
            fontWeight: 600,
            fontFamily: "sans-serif",
            overflow: "hidden",
          }}
        >
          Notes
        </Typography>
        <Box
          ref={homePagePageListRef}
          sx={{
            overflowY: "auto",
            marginBottom: "20px",
            "&::-webkit-scrollbar": {
              width: "0.4em",
            },
            "&::-webkit-scrollbar-track": {
              "-webkit-box-shadow": "inset 0 0 6px rgba(0,0,0,0.00)",
            },
            "&::-webkit-scrollbar-thumb": {
              backgroundColor: "rgba(0,0,100,.1)",
              borderRadius: "10px",
            },
          }}
        >
          {notes.map((note) => (
            <div
              key={note.id}
              style={{
                backgroundColor: colors.secondary,
                padding: "10px",
                borderRadius: "5px",
                marginBottom: "10px",
                position: "relative",
                display: "flex",
                alignItems: "center", // Align items vertically within each list item
                marginRight: "5px",
              }}
              onClick={() => navigate(`/note/${note.id}`)}
            >
              <div
                style={{
                  cursor: "pointer",
                  color: colors.primary, // Set the text color to primary color from the palette
                  flexGrow: 1,
                  marginRight: "10px",
                }}
              >
                <Typography
                  style={{
                    color: colors.primary,
                    lineHeight: "28px",
                    fontSize: "20px",
                    fontWeight: 400,
                    fontFamily: "sans-serif",
                    overflow: "hidden",
                    textOverflow: "ellipsis", // Add ellipsis for overflowed text
                    whiteSpace: "nowrap", // Prevent the text from wrapping to the next line
                    [theme.breakpoints.down("md")]: {
                      maxWidth: "150px", // Set different maxWidth for mobile view (for screens smaller than 'md' breakpoint)
                    },
                    [theme.breakpoints.up("md")]: {
                      maxWidth: "400px", // Default maxWidth for screens larger than or equal to 'md' breakpoint
                    },
                  }}
                >
                  {note.title}
                </Typography>
              </div>
              <IconButton
                color="secondary"
                onClick={(e) => {
                  e.stopPropagation(); // Prevent click propagation to the parent div
                  handleDeleteConfirmationOpen(note.id, note.title);
                }}
                style={{
                  position: "absolute",
                  top: "50%",
                  right: "10px",
                  transform: "translateY(-50%)",
                  color: colors.accent, // Set the icon color to accent color from the palette
                }}
              >
                <DeleteIcon />
              </IconButton>
            </div>
          ))}
        </Box>
        <Stack
          sx={{
            display: "flex",
            flexDirection: "row",
            justifyContent: "center", // Center the items horizontally
            position: "fixed",
            bottom: "20px",
            left: "50%", // Center the stack in the middle of the screen
            transform: "translateX(-50%)", // Center the stack in the middle of the screen
          }}
        >
          <Pagination
            count={Math.ceil((totalCount || 0) / itemsPerPage)}
            size="small"
            // On page number selection
            onChange={(event, value) => {
              setHomePageFetchNextPage({
                ...homePageFetchNextPage,
                page: value,
              });
              homePagePageListRef.current?.scrollTo(0, 0);
            }}
            sx={{
              // Adjust font size of the pagination buttons
              "& .MuiPaginationItem-root": {
                fontSize: "0.6rem",
                minWidth: "30px",
                height: "30px",
              },
            }}
          />
        </Stack>

        <Fab
          color="primary"
          aria-label="add"
          style={{
            position: "fixed",
            bottom: "20px",
            right: "20px",
            backgroundColor: colors.accent,
          }}
          onClick={handleOpenDialog}
        >
          <AddIcon style={{ color: colors.secondary }} />
        </Fab>

        <NoteDialogBox
          open={isDialogOpen}
          onClose={handleCloseDialog}
          onSave={handleSaveNote}
          title={editedNote.title}
          body={editedNote.body}
        />

        <DeleteConfirmationDialogBox
          onOpenDialogBox={deleteConfirmationOpen}
          onClickDeleteButton={handleDeleteNote}
          onCloseDialogBox={handleDeleteConfirmationClose}
          noteToDelete={{ id: noteToDelete.id, title: noteToDelete.title }}
        />
        <Stack
          direction={"row"}
          sx={{
            justifyContent: "center",
            // marginTop: '100px',
            // marginBottom: '20px',
          }}
        ></Stack>
      </Stack>
    </Box>
  );
};

export default HomePage;
