import React, { useEffect, useState } from "react";
import { Fab, TextField, Drawer, List, ListItem, IconButton } from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import MinimizeIcon from "@mui/icons-material/Minimize";
import { Note } from "../api";


const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [newNote, setNewNote] = useState({ title: "", body: "" });

  useEffect(() => {
    // Fetch notes from the API when the component mounts
    const fetchData = async () => {
      // Fetch your notes data here
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs once after initial render

  const handleDrawerOpen = () => {
    setDrawerOpen(true);
  };

  const handleDrawerClose = () => {
    setDrawerOpen(false);
  };

  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    handleDrawerClose();
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
        <h1 style={{ color: "#3f51b5", textAlign: "center", margin: "0" }}>Notes</h1>
        <IconButton onClick={handleDrawerOpen}>
          <MinimizeIcon />
        </IconButton>
      </div>
      <div style={{ display: "flex" }}>
        <Drawer anchor="left" open={drawerOpen} onClose={handleDrawerClose}>
          <List>
            {notes.map((note) => (
              <ListItem button key={note.id} onClick={() => handleNoteClick(note)}>
                {note.title}
              </ListItem>
            ))}
          </List>
        </Drawer>
        <div style={{ flex: 1, marginLeft: "20px" }}>
          {selectedNote ? (
            <div>
              <h2 style={{ fontSize: "18px", marginBottom: "10px" }}>{selectedNote.title}</h2>
              <p>{selectedNote.body}</p>
            </div>
          ) : (
            <p>Select a note from the drawer or create a new one.</p>
          )}
        </div>
      </div>
      <Fab
        color="primary"
        aria-label="add"
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: "#f50057",
        }}
        onClick={() => {
          // Handle add new note functionality here
        }}
      >
        <AddIcon style={{ color: "#fff" }} />
      </Fab>
    </div>
  );
};

export default HomePage;
