import React, { useEffect } from "react";
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
} from "@mui/material";
import colors from "../colors";

interface NoteDialogBoxProps {
  open: boolean;
  onClose: () => void;
  onSave: (noteData: { title: string; body: string }) => void;
  title: string;
  body: string;
}

const NoteDialogBox: React.FC<NoteDialogBoxProps> = ({
  open,
  onClose,
  onSave,
  title: initialTitle,
  body: initialBody,
}) => {
  const [title, setTitle] = React.useState(initialTitle);
  const [body, setBody] = React.useState(initialBody);
  const [isSaveDisabled, setIsSaveDisabled] = useState(true);

  useEffect(() => {
    // Enable or disable the "Save" button based on whether title and body are empty or not
    setIsSaveDisabled(!title.trim() || !body.trim());
  }, [title, body]);


  useEffect(() => {
    if (!open) {
      // Reset the internal state when the dialog is closed
      setTitle(initialTitle);
      setBody(initialBody);
    }
  }, [open, initialTitle, initialBody]);

  const handleSave = () => {
    onSave({ title, body });
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle
        style={{
          backgroundColor: colors.lightBackground,
          color: colors.primary,
        }}
      >
        {initialTitle ? "Edit Note" : "Add New Note"}
      </DialogTitle>
      <DialogContent
        style={{
          backgroundColor: colors.lightBackground,
          color: colors.primary,
        }}
      >
        <TextField
          fullWidth
          variant="outlined"
          margin="dense"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          sx={{ backgroundColor: colors.secondary }}
          InputProps={{
            style: {
              color: colors.primary,
              border: `1px solid ${colors.lightBackground}`,
            },
          }}
        />
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          margin="dense"
          value={body}
          onChange={(e) => setBody(e.target.value)}
          placeholder="Body"
          sx={{ backgroundColor: colors.secondary }}
          InputProps={{
            style: {
              color: colors.primary,
              border: `1px solid ${colors.lightBackground}`,
            },
          }}
        />
      </DialogContent>
      <DialogActions style={{ backgroundColor: colors.secondary }}>
        <Button onClick={onClose} sx={{ color: colors.accent }}>
          Cancel
        </Button>
        <Button
          onClick={handleSave}
          sx={{ color: colors.accent }}
          disabled={isSaveButtonDisabled}
        >
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default NoteDialogBox;
