import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
} from "@mui/material";
import colors from "../colors";
import { useState } from "react";

interface DeleteConfirmationDialogBoxProps {
  onCloseDialogBox: () => void;
  onClickCancelButton: () => void;
  noteToDelete: { id: number; title: string };
}

const DeleteConfirmationDialogBox: React.FC<
  DeleteConfirmationDialogBoxProps
> = ({ onClickCancelButton, onCloseDialogBox }) => {
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
  const [noteToDelete, setNoteToDelete] = useState({ id: -1, title: "" });

  const handleDeleteConfirmationClose = () => {
    setNoteToDelete({ id: -1, title: "" });
    setDeleteConfirmationOpen(false);
  };

  return (
    <Dialog open={deleteConfirmationOpen} onClose={onCloseDialogBox}>
      <DialogTitle
        style={{
          backgroundColor: colors.lightBackground,
          color: colors.primary,
        }}
      >
        Delete Note
      </DialogTitle>
      <DialogContent
        style={{
          backgroundColor: colors.lightBackground,
          color: colors.primary,
        }}
      >{`Are you sure you want to delete the note "${noteToDelete.title}"?`}</DialogContent>
      <DialogActions style={{ backgroundColor: colors.secondary }}>
        <Button
          onClick={onCloseDialogBox}
          sx={{ color: colors.accent }}
        >
          Cancel
        </Button>

        <Button onClick={onClickCancelButton} sx={{ color: colors.accent }}>
          Delete
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default DeleteConfirmationDialogBox;
