import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  Fab,
  TextField,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import MinimizeIcon from "@mui/icons-material/Minimize";
import { Note, createNote, deleteNote, getNotes } from "../api";

const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [drawerOpen, setDrawerOpen] = useState(true);
  const [newNote, setNewNote] = useState({ title: "", body: "" });
  const navigate = useNavigate();
  const MAX_BODY_LENGTH = 1000;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const notesData = await getNotes();
        setNotes(notesData);
      } catch (error) {
        console.error("Error fetching notes:", error);
      }
    };

    fetchData();
  }, []);

  const toggleDrawer = (open: boolean) => (event: React.KeyboardEvent | React.MouseEvent) => {
    if (event && event.type === "keydown" && ((event as React.KeyboardEvent).key === "Tab" || (event as React.KeyboardEvent).key === "Shift")) {
      return;
    }
    setDrawerOpen(open);
  };

  const handleSaveNote = async () => {
    try {
      const truncatedBody = newNote.body.slice(0, MAX_BODY_LENGTH);
      const savedNote = await createNote({
        title: newNote.title,
        body: truncatedBody,
      });

      const updatedNotes = await getNotes();
      setNotes(updatedNotes);
      setDrawerOpen(false);
      setNewNote({ title: "", body: "" });
    } catch (error) {
      console.error("Error creating note:", error);
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={toggleDrawer(false)}
        variant="temporary"
        ModalProps={{
          keepMounted: true,
        }}
      >
        <List>
          <ListItem button component={Link} to="/note">
            <ListItemText primary="Notes" />
          </ListItem>
          {/* Add more list items as needed */}
        </List>
      </Drawer>

      {/* Content */}
      {notes.map((note) => (
        <div key={note.id}>
          {/* Note content rendering */}
        </div>
      ))}

      <IconButton
        style={{
          position: "fixed",
          bottom: "20px",
          left: "20px",
          backgroundColor: "#f50057",
          zIndex: 1,
        }}
        onClick={toggleDrawer(true)}
      >
        <MinimizeIcon style={{ color: "#fff" }} />
      </IconButton>

      <Fab
        color="primary"
        aria-label="add"
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: "#f50057",
        }}
        onClick={() => setDrawerOpen(true)}
      >
        <AddIcon style={{ color: "#fff" }} />
      </Fab>
    </div>
  );
};

export default HomePage;
