import React, { useEffect, useState } from "react";
import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
} from "@mui/material";
import colors from "../colors";

interface NoteDialogBoxProps {
  open: boolean;
  onClose: () => void;
  onSave: (noteData: { title: string; body: string }) => void;
  title: string;
  body: string;
}

const NoteDialogBox: React.FC<NoteDialogBoxProps> = ({
  open,
  onClose,
  onSave,
  title: initialTitle,
  body: initialBody,
}) => {
  const [title, setTitle] = React.useState(initialTitle);
  const [body, setBody] = React.useState(initialBody);
  const [isSaveDisabled, setIsSaveDisabled] = useState(true);
  const MAX_CHARACTERS = 1000;
  const MAX_TITLE_CHARACTERS = 150;

  useEffect(() => {
    // Enable or disable the "Save" button based on whether title and body are empty or not
    setIsSaveDisabled(!title.trim() || !body.trim());
  }, [title, body]);

  const handleTitleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newTitle = e.target.value.slice(0, MAX_TITLE_CHARACTERS);
    setTitle(newTitle);
    setIsSaveDisabled((prevState) => !newTitle.trim() || !body.trim());
  };

  const handleBodyChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newBody = e.target.value.slice(0, MAX_CHARACTERS);
    setBody(newBody);
    setIsSaveDisabled((prevState) => !title.trim() || !newBody.trim());
  };

  useEffect(() => {
    if (!open) {
      // Reset the internal state when the dialog is closed
      setTitle(initialTitle);
      setBody(initialBody);
      setIsSaveDisabled(true);
    }
  }, [open, initialTitle, initialBody]);

  const handleSave = () => {
    onSave({ title, body });
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle
        sx={{
          backgroundColor: colors.lightBackground,
          color: colors.primary,
          lineHeight: "30px",
          fontSize: "28px",
          fontWeight: 400,
          fontFamily: "sans-serif",
        }}
      >
        {initialTitle ? "Edit Note" : "Add New Note"}
      </DialogTitle>
      <DialogContent
        sx={{
          backgroundColor: colors.lightBackground,
          color: colors.primary,
        }}
      >
        <TextField
          fullWidth
          variant="outlined"
          margin="dense"
          value={title}
          onChange={handleTitleChange}
          placeholder="Title"
          sx={{ backgroundColor: colors.secondary }}
          InputProps={{
            style: {
              color: colors.primary,
              lineHeight: "28px",
              fontSize: "20px",
              fontWeight: 400,
              fontFamily: "sans-serif",
              border: `1px solid ${colors.lightBackground}`,
            },
          }}
        />
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          margin="dense"
          value={body}
          onChange={handleBodyChange}
          placeholder="Body"
          InputProps={{
            sx: {
              color: colors.primary,
              lineHeight: "28px",
              fontSize: "20px",
              fontWeight: 400,
              fontFamily: "sans-serif",
              border: `1px solid ${colors.lightBackground}`,
              scrollbarWidth: "thin", // Set the width of the scrollbar for Firefox
              scrollbarColor: `${colors.primary} ${colors.lightBackground}`, // Set the color of the scrollbar for Firefox
              WebkitOverflowScrolling: "touch", // Enable smooth scrolling on iOS devices
              "&::-webkit-scrollbar": {
                width: "3px", // Width of the scrollbar track
              },
              "&::-webkit-scrollbar-thumb": {
                backgroundColor: colors.primary, // Color of the scrollbar thumb
              },
              flex: 1,
              overflow: "auto",
            },
          }}
          sx={{
            backgroundColor: colors.secondary,
          }}
        />

        <div
          style={{
            textAlign: "right",
            color: colors.primary,
            lineHeight: "22px",
            fontSize: "16px",
            fontWeight: 200,
            opacity: 0.6,
            fontFamily: "sans-serif",
          }}
        >
          {MAX_CHARACTERS - body.length} characters left
        </div>
      </DialogContent>
      <DialogActions style={{ backgroundColor: colors.secondary }}>
        <Button onClick={onClose} sx={{ color: colors.accent , 
            lineHeight: "26px",
            fontSize: "20px",
            fontWeight: 600,
            opacity: 0.6,
            fontFamily: "sans-serif", }}>
          Cancel
        </Button>
        <Button
          onClick={handleSave}
          sx={{ color: colors.accent }}
          disabled={isSaveDisabled}
        >
          Save
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default NoteDialogBox;
