import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  IconButton,
  colors,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import { Note, createNote, deleteNote, getNotes } from "../api";


const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
  const [noteToDelete, setNoteToDelete] = useState({ id: -1, title: "" });
  const [newNote, setNewNote] = useState({ title: "", body: "" });
  const navigate = useNavigate();
  const [isSaveButtonDisabled, setIsSaveButtonDisabled] = useState(true);
  const MAX_body_LENGTH = 1000;

  useEffect(() => {
    // Fetch notes from the API when the component mounts
    const fetchData = async () => {
      try {
        const notesData = await getNotes();
        setNotes(notesData);
      } catch (error) {
        // Handle error fetching notes
        console.error("Error fetching notes:", error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures the effect runs once after initial render

  const handleDialogOpen = () => {
    setDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
  };

  const handleDeleteConfirmationOpen = (id: number, title: string) => {
    setNoteToDelete({ id, title });
    setDeleteConfirmationOpen(true);
  };

  const handleDeleteConfirmationClose = () => {
    setNoteToDelete({ id: -1, title: "" });
    setDeleteConfirmationOpen(false);
  };

  const handleDeleteNote = async () => {
    try {
      // Delete note using the API function
      await deleteNote(noteToDelete.id);
      const updatedNotes = notes.filter((note) => note.id !== noteToDelete.id);
      setNotes(updatedNotes);
      handleDeleteConfirmationClose();
    } catch (error) {
      // Handle error deleting note
      console.error("Error deleting note:", error);
    }
  };

  const handleSaveNote = async () => {
    try {
      // Truncate body if it exceeds 1000 characters
      const truncatedbody = newNote.body.slice(0, MAX_body_LENGTH);

      const savedNote = await createNote({
        title: newNote.title,
        body: truncatedbody,
      });

      // Fetch the updated list of notes after creating the new note
      const updatedNotes = await getNotes();
      setNotes(updatedNotes);
      setDialogOpen(false);
      setNewNote({ title: "", body: "" });
    } catch (error) {
      // Handle error creating note
      console.error("Error creating note:", error);
    }
  };

  return (
    <div
      style={{
        padding: "20px",
        maxWidth: "600px",
        margin: "0 auto",
        backgroundColor: colors.lightBackground,
      }}
    >
      <h1
        style={{
          color: colors.primary,
          textAlign: "center",
          marginBottom: "20px",
        }}
      >
        Notes
      </h1>
      {notes.map((note) => (
        <div key={note.id}>
          <div
            style={{
              backgroundColor: "#f5f5f5",
              padding: "10px",
              borderRadius: "5px",
              marginBottom: "10px",
              position: "relative",
            }}
            onClick={() => navigate(`/note/${note.id}`)}
          >
            <div
              style={{
                cursor: "pointer",
                color: "#333",
                flexGrow: 1,
                marginRight: "10px",
              }}
            >
              <h2 style={{ margin: "0", fontSize: "18px" }}>{note.title}</h2>
            </div>
            <IconButton
              color="secondary"
              onClick={(e) => {
                e.stopPropagation(); // Prevent click propagation to the parent div
                handleDeleteConfirmationOpen(note.id, note.title);
              }}
              style={{
                position: "absolute",
                top: "50%",
                right: "10px",
                transform: "translateY(-50%)",
              }}
            >
              <DeleteIcon />
            </IconButton>
          </div>
        </div>
      ))}

      <Fab
        color="primary"
        aria-label="add"
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: colors.accent,
        }}
        onClick={handleDialogOpen}
      >
        <AddIcon style={{ color: colors.secondary }} />
      </Fab>

      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle>Add New Note</DialogTitle>
        <DialogContent>
          <TextField
            label="Title"
            fullWidth
            margin="normal"
            variant="outlined"
            value={newNote.title}
            onChange={(e) => {
              setNewNote({ ...newNote, title: e.target.value });
              setIsSaveButtonDisabled(
                e.target.value.trim() === "" || newNote.body.trim() === ""
              );
            }}
          />
          <TextField
            label="Body"
            fullWidth
            multiline
            rows={4}
            margin="normal"
            variant="outlined"
            value={newNote.body}
            onChange={(e) => {
              setNewNote({ ...newNote, body: e.target.value });
              setIsSaveButtonDisabled(
                newNote.title.trim() === "" || e.target.value.trim() === ""
              );
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} color="secondary">
            Cancel
          </Button>
          <Button
            onClick={handleSaveNote}
            variant="contained"
            color="primary"
            disabled={isSaveButtonDisabled} // Disable the button when either title or body is empty
          >
            Save
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog
        open={deleteConfirmationOpen}
        onClose={handleDeleteConfirmationClose}
      >
        <DialogTitle>Delete Note</DialogTitle>
        <DialogContent>{`Are you sure you want to delete the note "${noteToDelete.title}"?`}</DialogContent>
        <DialogActions>
          <Button onClick={handleDeleteConfirmationClose} color="secondary">
            Cancel
          </Button>
          <Button
            onClick={handleDeleteNote}
            variant="contained"
            color="primary"
          >
            Delete
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default HomePage;
