import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import {
  Fab,
  TextField,
  List,
  ListItem,
  ListItemText,
  IconButton,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";
import MinimizeIcon from "@mui/icons-material/Minimize";
import { Note, createNote, deleteNote, getNotes } from "../api";

const HomePage: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [isColumnMinimized, setIsColumnMinimized] = useState(false);
  const [newNote, setNewNote] = useState({ title: "", body: "" });
  const navigate = useNavigate();
  const MAX_BODY_LENGTH = 1000;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const notesData = await getNotes();
        setNotes(notesData);
      } catch (error) {
        console.error("Error fetching notes:", error);
      }
    };

    fetchData();
  }, []);

  const handleMinimizeToggle = () => {
    setIsColumnMinimized(!isColumnMinimized);
  };

  const handleSaveNote = async () => {
    try {
      const truncatedBody = newNote.body.slice(0, MAX_BODY_LENGTH);
      const savedNote = await createNote({
        title: newNote.title,
        body: truncatedBody,
      });

      const updatedNotes = await getNotes();
      setNotes(updatedNotes);
      setNewNote({ title: "", body: "" });
    } catch (error) {
      console.error("Error creating note:", error);
    }
  };

  return (
    <div style={{ display: "flex", maxWidth: "600px", margin: "0 auto" }}>
      {!isColumnMinimized && (
        <div style={{ width: "200px", backgroundColor: "#f5f5f5", padding: "20px" }}>
          {/* Left Column Content */}
          <List>
            <ListItem button component={Link} to="/note">
              <ListItemText primary="Notes" />
            </ListItem>
            {/* Add more list items as needed */}
          </List>
        </div>
      )}

      <div style={{ flex: 1, padding: "20px" }}>
        {/* Content */}
        {notes.map((note) => (
          <div key={note.id}>
            {/* Note content rendering */}
          </div>
        ))}
      </div>

      {!isColumnMinimized && (
        <IconButton
          style={{
            position: "fixed",
            bottom: "20px",
            left: "20px",
            backgroundColor: "#f50057",
            zIndex: 1,
          }}
          onClick={handleMinimizeToggle}
        >
          <MinimizeIcon style={{ color: "#fff" }} />
        </IconButton>
      )}

      <Fab
        color="primary"
        aria-label="add"
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          backgroundColor: "#f50057",
        }}
        onClick={handleSaveNote}
      >
        <AddIcon style={{ color: "#fff" }} />
      </Fab>
    </div>
  );
};

export default HomePage;
